*,
*::before,
*::after {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body,
html {
  width: 100%;
  line-height: 1.6;
  font-family: $f-primary;
  font-size: var(--fs-body);
  color: var(--clr-grey);
}

body {
  height: 100%;
}

main {
  overflow-x: hidden;
}

p {
  font-family: $f-second;
  line-height: 1.6;
}

nav {
  all: unset;
}

input {
  -webkit-box-sizing: border-box !important; /* Safari/Chrome, other WebKit */
  -moz-box-sizing: border-box !important; /* Firefox, other Gecko */
  box-sizing: border-box !important;
}

.section {
  padding: 5em 1em;
}

.mx-width {
  width: 100%;
  max-width: 1200px;
  margin: auto;
}

// Icons Default Styles
.icon {
  color: var(--clr-primary);
  font-size: var(--fs-icon);
  cursor: pointer;
  @include cubicBezier;

  &:hover,
  &:focus {
    color: var(--clr-grey);
  }
}

// BTN Default Style
.btn {
  background-color: var(--clr-primary);
  color: var(--clr-white);
  font-size: 0.6rem;
  font-weight: var(--fw-bold);
  letter-spacing: 3px;
  padding: 0.8em 4.1em;
  text-align: center;
  border: 2px solid transparent;
  text-transform: uppercase;
  cursor: pointer;
  display: inline-flex;
  // width: 100%;
  justify-content: center;
  align-items: center;
  line-height: 1;
  @include cubicBezier;

  &:hover,
  &:focus {
    background-color: transparent;
    border: 2px solid var(--clr-primary);
    color: var(--clr-primary);
    transform: scale(0.96) translateY(4px);
  }
}

.btn--outline {
  background-color: transparent;
  border: 2px solid var(--clr-primary);
  color: var(--clr-white);

  &:hover,
  &:focus {
    background-color: var(--clr-primary);
    color: var(--clr-white);
  }
}

// TITLES STYLE
.title {
  font-weight: bold;
  margin-bottom: 1em;
  .top {
    text-transform: capitalize;
    color: var(--clr-primary);
    font-size: var(--fs-h3);
    margin-bottom: 0.3em;
  }
  .bottom {
    position: relative;
    text-transform: uppercase;
    font-size: var(--fs-h2);
    letter-spacing: 0.7px;
    &::before {
      content: '';
      position: absolute;
      bottom: -6px;
      left: 0;
      height: 3.3px;
      width: 3em;
      // background-color: var(--clr-primary);
      @include animateBg;
    }
  }
}

// PARA STYLE
.para {
  color: var(--clr-grey);
  margin: 1.5em 0;
  line-height: 1.7;
}

// ENAIL ICON TEXT
.email--box {
  display: flex;
  align-items: center;
  font-weight: var(--fw-semi);
  font-size: 0.9rem;
  @include cubicBezier;
  cursor: pointer;
  i {
    margin-right: 0.8em;
  }

  &:hover,
  &:focus {
    color: var(--clr-primary);
  }
}

.addBox {
  padding: 13em 1em 15em;

  &::before,
  &::after {
    content: '';
    position: absolute;
    background: url('../img/bg/box.png');
    width: 100%;
    left: 0;
    right: 0;
    height: 100px;
    background-position: center;
    background-size: cover;
  }

  &::before {
    top: -3px;
    transform: scaleY(-1);
    -webkit-transform: scaleY(-1);
  }
  &::after {
    bottom: -3px;
  }
}

// Animated Gradient
.animated-gradient {
  background: linear-gradient(
    -65deg,
    var(--clr-primary),
    #c66034,
    var(--clr-primary),
    #c25729
  );
  background-size: 400% 400%;
  @include allAnimationKits(gradientBG 15s ease infinite);
}

@-webkit-keyframes gradientBG {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@keyframes gradientBG {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@-webkit-keyframes pulse {
  from {
    -webkit-transform: scale(1) translate(-50%, -50%) rotate(0deg);
    transform: scale(1) translate(-50%, -50%) rotate(0deg);
  }
  to {
    -webkit-transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
    transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
  }
}
@keyframes pulse {
  from {
    -webkit-transform: scale(1) translate(-50%, -50%) rotate(0deg);
    transform: scale(1) translate(-50%, -50%) rotate(0deg);
  }
  to {
    -webkit-transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
    transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
  }
}

// INITIAL LOAD SCREEN

.load-screen {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  height: 100vh;
  min-height: 100vh;
  width: 100%;
  z-index: 2000;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  font-size: 10vh;
  pointer-events: none;
  background: linear-gradient(
    -20deg,
    var(--clr-dark-light),
    var(--clr-dark),
    var(--clr-dark-light),
    var(--clr-dark)
  );
  background-size: 400% 400%;
  @include allAnimationKits(gradientBG 5s alternate infinite ease-in-out);

  span {
    opacity: 0;
    font-weight: var(--fw-black);
    @include allAnimationKits(loadPulse 0.75s alternate infinite ease-in-out);

    &:nth-child(2) {
      animation-delay: 0.2s;
    }
    &:last-child {
      animation-delay: 0.3s;
    }
  }
}

@-webkit-keyframes loadPulse {
  to {
    -webkit-transform: scale(0.8);
    transform: scale(0.8);
    opacity: 0;
  }
}
@keyframes loadPulse {
  to {
    -webkit-transform: scale(0.8);
    transform: scale(0.8);
    opacity: 0.5;
  }
}

@include tabletLg {
  // Curser Styles
  .fallow--curser {
    position: absolute;
    border: 2px solid var(--clr-white);
    width: 3rem;
    height: 3rem;
    border-radius: 50%;
    transform: translate(-50%, -50%);
    pointer-events: none;
    z-index: 1000;
  }

  .cursor-animate {
    border-color: var(--clr-primary);
    border-top-color: rgba($clr-primary, 0.05);
    border-bottom-color: rgba($clr-primary, 0.05);
    height: 2rem;
    width: 2rem;
    @include allAnimationKits(pulse 0.7s linear infinite alternate);
  }
}

