.portfolio {
  @include bgIMG($clr-dark-light, 0.98);
  padding: 10em 1em;
  @include divederLines;

  &--content {
    max-width: 1150px;
    margin: auto;
  }
}

.portfolio--content__card {
  margin-bottom: 2.5em;
}

.carousel {
  overflow: visible;
  .carousel-item {
    display: grid;
    place-content: center;

    img {
      height: auto;
    }
  }
}

.screen-container {
  --screen-w: 22em;
  --screen-h: 201px;
  --page-w: 85%;
  --page-m: 1em auto 0.6em;

  display: grid;
  overflow: hidden;
  width: var(--screen-w);
  height: var(--screen-h);

  .screen,
  .page {
    grid-area: 1 / -1;
  }

  .page {
    overflow: hidden;
    height: auto;
    margin: var(--page-m);
    width: var(--page-w);
    z-index: -1;

    img {
      background-color: black;
    }
  }
}

.carousel-item.active {
  .page img {
    @include allAnimationKits(scrollPage 20s 1s linear infinite alternate);
  }

  .mobile .page img {
    @include allAnimationKits(scrollPage 30s 1s linear infinite alternate);
  }
}

.portfolio--content__card:nth-of-type(4) .carousel-item.active {
  .page img {
    @include allAnimationKits(scrollPage 15s 1s linear infinite alternate);
  }

  .laptop .page img {
    @include allAnimationKits(scrollPageKeep 5s 1s linear infinite alternate);
  }
}

.laptop {
  --page-w: 75%;
}

.tablet {
  --screen-w: 13em;
  --screen-h: 270px;
  --page-m: 1.5em auto 0.6em;
}

.mobile {
  --screen-w: 9em;
  --screen-h: 286px;
  --page-m: 0.72em auto 0.6em;
}

.portfolio--content__card--info {
  display: grid;
  grid-template-columns: 1fr auto;
  .info--title {
    grid-column: 1 / 2;
    position: relative;
    color: var(--clr-primary);
    font-weight: var(--fw-bold);
    font-size: var(--fs-h3);
    letter-spacing: 0.7px;
    text-transform: uppercase;
    &::before {
      content: '';
      position: absolute;
      bottom: 1px;
      left: 0;
      height: 3.3px;
      width: 4em;
      background-color: var(--clr-white);
    }
  }

  .github {
    grid-column: 2 / 3;
    justify-self: end;
    margin-right: 2em;
  }

  .para,
  .keys,
  .btn {
    grid-column: 1 / -1;
  }

  .keys {
    margin-bottom: 2.5em;
    span {
      color: var(--clr-grey);
      padding: 0.35em 0.4em;
      font-size: 0.9rem;
      font-weight: var(--fw-semi);
      text-transform: capitalize;
      border-radius: 5px;
      margin: 0.3em 0.5em 0.3em 0;
      display: inline-block;
      &:first-child {
        margin-left: 0;
      }
      &:last-child {
        margin-right: 0;
      }
    }
  }

  .btn {
    justify-self: center;
  }
}

@include mediaTablet {
  .portfolio {
    padding: 13em 1em;
  }
  .portfolio--content__card {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    align-items: center;
    grid-gap: 1em;
    gap: 1em;

    &:nth-child(even) &--screens {
      grid-area: 1 / 2 / 1 / 3;
    }

    &:nth-child(even) &--info {
      justify-self: start;
    }

    &--info {
      align-self: center;
      grid-template-columns: auto 1fr;
      align-items: center;
      max-width: 32em; // 512px
      justify-self: end;
      .info--title {
        grid-column: 1 / -1;
        &::before {
          bottom: -6px;
        }
      }

      .github {
        grid-area: 4 / 2 / 4 / 3;
        margin-left: 2em;
        justify-self: start;
      }

      .btn {
        grid-column: 1 / 2;
        justify-self: start;
      }

      .keys {
        margin-bottom: 2em;
      }
    }
  }
}

@include tabletLg {
  .portfolio--content__card {
    grid-gap: 0em;
    gap: 0em;
  }
}

// Screen Animation KeyFrames

@-webkit-keyframes scrollPage {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 19em));
    transform: translateY(calc(-100% + 19em));
  }
}
@keyframes scrollPage {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 19em));
    transform: translateY(calc(-100% + 19em));
  }
}

@-webkit-keyframes scrollPageKeep {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 10em));
    transform: translateY(calc(-100% + 10em));
  }
}
@keyframes scrollPageKeep {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 10em));
    transform: translateY(calc(-100% + 10em));
  }
}

