.header {
  position: absolute;
  left: 0;
  right: 0;
}

.nav {
  position: relative;
  display: flex;
  align-items: center;
  padding: 1em;
  background-color: transparent;
  z-index: 10;
  @include easeIn;

  &--logo {
    margin-right: auto;
    margin-left: 1em;
    width: 40px;
  }

  &--toggle {
    position: relative;
    background-color: transparent;
    padding: 0.5em;
    cursor: pointer;

    &:focus {
      background: transparent;
    }

    &:hover,
    &:focus {
      .hamburger,
      .hamburger::before,
      .hamburger::after {
        background: var(--clr-primary);
      }
    }
  }

  &--portfolio {
    width: fit-content;
    padding: 0.2em 1.8em;
    border-radius: 5px;
    font-size: 0.7rem;
    color: var(--clr-white);
    background-color: var(--clr-primary);
    border: 2px solid transparent;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    font-weight: var(--fw-bold);
    margin-right: 5em;
    @include cubicBezier;

    &:hover,
    &:focus {
      opacity: 0.8;
      border-color: var(--clr-primary);
      background-color: transparent;
    }

    i {
      margin-left: 0.5em;
    }
  }

  .btn {
    display: none;
    color: var(--clr-primary);
    padding: 0 2.5em;
    line-height: 1;

    &:hover,
    &:focus {
      background-color: transparent;
      color: var(--clr-white);
    }
  }

  &--container {
    background-color: var(--clr-dark);
    position: absolute;
    width: 100%;
    top: 100%;
    height: 0;
    left: 0;
    right: 0;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.12);
    @include easeIn;

    .nav--links {
      position: relative;
      @include cubicBezier;
      opacity: 0;
      transform: scale(0);
      color: var(--clr-white);
      display: block;
      padding: 0.5em 0;
      padding-left: 2em;
      width: 100%;
      height: 100%;
      border-bottom: 1px solid rgba($clr-white, 0.2);

      &:last-child {
        border-bottom: none;
      }

      &:hover,
      &:focus {
        color: var(--clr-primary);
      }
    }
  }
}

.hamburger,
.hamburger::before,
.hamburger::after {
  height: 3px;
  background: var(--clr-white);
  @include easeIn;
  // transition: transform 350ms ease-in-out, opacity 200ms linear;
}

.hamburger {
  display: block;
  position: relative;
  width: 2em;
  cursor: pointer;

  &::before,
  &::after {
    content: '';
    position: absolute;
    left: 0;
    z-index: -1;
  }

  &::before {
    bottom: 6px;
    width: 1em;
  }
  &::after {
    top: 6px;
    width: 1em;
    margin-left: 1em;
  }
}

/* Navigation open styles */

.nav-open {
  .nav {
    background-color: var(--clr-dark);
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.12);

    // transition: none;
  }

  .hamburger,
  .hamburger::before,
  .hamburger::after {
    background: var(--clr-primary);
  }

  .nav--container {
    height: 13.17em;

    .nav--links {
      opacity: 1;
      transform: scale(1);
    }
  }

  .hamburger {
    transform: rotate(45deg);

    &::before {
      transform: rotate(90deg) translate(-1px, -6px);
    }

    &::after {
      transform: rotate(-90deg) translate(-1px, -9px);
    }
  }
}

.scrollNav {
  position: fixed;
  z-index: 50;
  background-color: var(--clr-dark);
  box-shadow: 0 0 15px rgba(0, 0, 0, 0.12);
  @include allAnimationKits(easeIN 0.3s forwards ease-in-out);

  @-webkit-keyframes easeIN {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
  @keyframes easeIN {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
}

@include mediaTablet {
  .nav {
    &--logo {
      width: 47px;
    }

    &--portfolio {
      font-size: 0.75rem;
      margin-right: 7em;
    }

    &--container {
      .nav--links {
        padding: 0.5em 0;
        padding-left: 2em;
      }
    }
  }
}

@include tabletLg {
  .nav {
    &--toggle,
    &--portfolio {
      display: none;
    }

    .btn {
      display: inline-flex;
    }

    &--container {
      display: flex;
      position: relative;
      height: auto;
      flex-direction: row;
      background-color: transparent;
      justify-content: space-between;
      width: 550px;
      margin-right: 3em;
      text-align: center;
      background-color: transparent;
      box-shadow: none;

      .nav--links {
        opacity: 1;
        transform: scale(1);
        color: var(--clr-white);
        display: block;
        padding: 0 0.2em;
        width: auto;
        height: auto;
        border-bottom: 2px solid transparent;
        font-weight: var(--fw-bold);

        &::before {
          content: '';
          @include cubicBezier;
          position: absolute;
          background-color: var(--clr-primary);
          height: 2px;
          bottom: 0;
          left: 0;
          right: 0;
          transform: scale(0);
          transform-origin: center;
        }

        &:hover::before,
        &:focus::before {
          transform: scale(1);
        }

        &:hover,
        &:focus {
          color: var(--clr-white);
        }

        &.active {
          color: var(--clr-white);

          &::before,
          &::before {
            transform: scale(1);
          }
        }
      }
    }
  }
}

