sources.scss\\2-base\\_global.scss:
*,
*::before,
*::after {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body,
html {
  width: 100%;
  line-height: 1.6;
  font-family: $f-primary;
  font-size: var(--fs-body);
  color: var(--clr-grey);
}

body {
  height: 100%;
}

p {
  font-family: $f-second;
  line-height: 1.6;
}

nav {
  all: unset;
}

input {
  -webkit-box-sizing: border-box !important; /* Safari/Chrome, other WebKit */
  -moz-box-sizing: border-box !important; /* Firefox, other Gecko */
  box-sizing: border-box !important;
}

.section {
  padding: 5em 1em;
}

.mx-width {
  width: 100%;
  max-width: 1200px;
  margin: auto;
}

// Icons Default Styles
.icon {
  color: var(--clr-primary);
  font-size: var(--fs-icon);
  cursor: pointer;
  @include cubicBezier;

  &:hover,
  &:focus {
    color: var(--clr-grey);
  }
}

// BTN Default Style
.btn {
  background-color: var(--clr-primary);
  color: var(--clr-white);
  font-size: 0.6rem;
  font-weight: var(--fw-bold);
  letter-spacing: 3px;
  padding: 0.8em 4.1em;
  text-align: center;
  border: 2px solid transparent;
  text-transform: uppercase;
  cursor: pointer;
  display: inline-flex;
  // width: 100%;
  justify-content: center;
  align-items: center;
  line-height: 1;
  @include cubicBezier;

  &:hover,
  &:focus {
    background-color: transparent;
    border: 2px solid var(--clr-primary);
    color: var(--clr-primary);
    transform: scale(0.96) translateY(4px);
  }
}

.btn--outline {
  background-color: transparent;
  border: 2px solid var(--clr-primary);
  color: var(--clr-white);

  &:hover,
  &:focus {
    background-color: var(--clr-primary);
    color: var(--clr-white);
  }
}

// TITLES STYLE
.title {
  font-weight: bold;
  margin-bottom: 1em;
  .top {
    text-transform: capitalize;
    color: var(--clr-primary);
    font-size: var(--fs-h3);
    margin-bottom: 0.3em;
  }
  .bottom {
    position: relative;
    text-transform: uppercase;
    font-size: var(--fs-h2);
    letter-spacing: 0.7px;
    &::before {
      content: '';
      position: absolute;
      bottom: -6px;
      left: 0;
      height: 3.3px;
      width: 3em;
      // background-color: var(--clr-primary);
      @include animateBg;
    }
  }
}

// PARA STYLE
.para {
  color: var(--clr-grey);
  margin: 1.5em 0;
  line-height: 1.7;
}

// ENAIL ICON TEXT
.email--box {
  display: flex;
  align-items: center;
  font-weight: var(--fw-semi);
  font-size: 0.9rem;
  @include cubicBezier;
  cursor: pointer;
  i {
    margin-right: 0.8em;
  }

  &:hover,
  &:focus {
    color: var(--clr-primary);
  }
}

.addBox {
  padding: 13em 1em 15em;

  &::before,
  &::after {
    content: '';
    position: absolute;
    background: url('../img/bg/box.png');
    width: 100%;
    left: 0;
    right: 0;
    height: 100px;
    background-position: center;
    background-size: cover;
  }

  &::before {
    top: -3px;
    transform: scaleY(-1);
    -webkit-transform: scaleY(-1);
  }
  &::after {
    bottom: -3px;
  }
}

// Animated Gradient
.animated-gradient {
  background: linear-gradient(
    -65deg,
    var(--clr-primary),
    #c66034,
    var(--clr-primary),
    #c25729
  );
  background-size: 400% 400%;
  @include allAnimationKits(gradientBG 15s ease infinite);
}

@-webkit-keyframes gradientBG {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@keyframes gradientBG {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

// Curser Styles
.fallow--curser {
  position: absolute;
  border: 2px solid var(--clr-white);
  width: 3rem;
  height: 3rem;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  z-index: 1000;
}

.cursor-animate {
  border-color: var(--clr-primary);
  border-top-color: rgba($clr-primary, 0.05);
  border-bottom-color: rgba($clr-primary, 0.05);
  height: 2rem;
  width: 2rem;
  @include allAnimationKits(pulse 0.7s linear infinite alternate);
}

@-webkit-keyframes pulse {
  from {
    -webkit-transform: scale(1) translate(-50%, -50%) rotate(0deg);
    transform: scale(1) translate(-50%, -50%) rotate(0deg);
  }
  to {
    -webkit-transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
    transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
  }
}
@keyframes pulse {
  from {
    -webkit-transform: scale(1) translate(-50%, -50%) rotate(0deg);
    transform: scale(1) translate(-50%, -50%) rotate(0deg);
  }
  to {
    -webkit-transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
    transform: scale(0.8) translate(-50%, -50%) rotate(-360deg);
  }
}

// INITIAL LOAD SCREEN

.load-screen {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  height: 100vh;
  min-height: 100vh;
  width: 100%;
  z-index: 2000;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  font-size: 10vh;
  pointer-events: none;
  background: linear-gradient(
    -20deg,
    var(--clr-dark-light),
    var(--clr-dark),
    var(--clr-dark-light),
    var(--clr-dark)
  );
  background-size: 400% 400%;
  @include allAnimationKits(gradientBG 5s alternate infinite ease-in-out);

  span {
    opacity: 0;
    font-weight: var(--fw-black);
    @include allAnimationKits(loadPulse 0.75s alternate infinite ease-in-out);

    &:nth-child(2) {
      animation-delay: 0.2s;
    }
    &:last-child {
      animation-delay: 0.3s;
    }
  }
}

@-webkit-keyframes loadPulse {
  to {
    -webkit-transform: scale(0.8);
    transform: scale(0.8);
    opacity: 0;
  }
}
@keyframes loadPulse {
  to {
    -webkit-transform: scale(0.8);
    transform: scale(0.8);
    opacity: 0.5;
  }
}

sources.scss\\3-layout\\_portfolio.scss:
.portfolio {
  @include bgIMG($clr-dark-light, 0.98);
  padding: 10em 1em;
  @include divederLines;

  &--content {
    max-width: 1150px;
    margin: auto;
  }
}

.portfolio--content__card {
  margin-bottom: 2.5em;
}

.carousel {
  overflow: visible;
  .carousel-item {
    display: grid;
    place-content: center;

    img {
      height: auto;
    }
  }
}

.screen-container {
  --screen-w: 22em;
  --screen-h: 201px;
  --page-w: 85%;
  --page-m: 1em auto 0.6em;

  display: grid;
  overflow: hidden;
  width: var(--screen-w);
  height: var(--screen-h);

  .screen,
  .page {
    grid-area: 1 / -1;
  }

  .page {
    overflow: hidden;
    height: auto;
    margin: var(--page-m);
    width: var(--page-w);
    z-index: -1;

    img {
      background-color: black;
    }
  }
}

.carousel-item.active {
  .page img {
    @include allAnimationKits(scrollPage 20s 1s linear infinite alternate);
  }

  .mobile .page img {
    @include allAnimationKits(scrollPage 30s 1s linear infinite alternate);
  }
}

.portfolio--content__card:nth-of-type(4) .carousel-item.active {
  .page img {
    @include allAnimationKits(scrollPage 15s 1s linear infinite alternate);
  }

  .laptop .page img {
    @include allAnimationKits(scrollPageKeep 5s 1s linear infinite alternate);
  }
}

.laptop {
  --page-w: 75%;
}

.tablet {
  --screen-w: 13em;
  --screen-h: 270px;
  --page-m: 1.5em auto 0.6em;
}

.mobile {
  --screen-w: 9em;
  --screen-h: 286px;
  --page-m: 0.72em auto 0.6em;
}

.portfolio--content__card--info {
  display: grid;
  grid-template-columns: 1fr auto;
  .info--title {
    grid-column: 1 / 2;
    position: relative;
    color: var(--clr-primary);
    font-weight: var(--fw-bold);
    font-size: var(--fs-h3);
    letter-spacing: 0.7px;
    text-transform: uppercase;
    &::before {
      content: '';
      position: absolute;
      bottom: 1px;
      left: 0;
      height: 3.3px;
      width: 4em;
      background-color: var(--clr-white);
    }
  }

  .github {
    grid-column: 2 / 3;
    justify-self: end;
    margin-right: 2em;
  }

  .para,
  .keys,
  .btn {
    grid-column: 1 / -1;
  }

  .keys {
    margin-bottom: 2.5em;
    span {
      color: var(--clr-grey);
      padding: 0.35em 0.4em;
      font-size: 0.9rem;
      font-weight: var(--fw-semi);
      text-transform: capitalize;
      border-radius: 5px;
      margin: 0.3em 0.5em 0.3em 0;
      display: inline-block;
      &:first-child {
        margin-left: 0;
      }
      &:last-child {
        margin-right: 0;
      }
    }
  }

  .btn {
    justify-self: center;
  }
}

@include mediaTablet {
  .portfolio {
    padding: 13em 1em;
  }
  .portfolio--content__card {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    align-items: center;
    grid-gap: 1em;
    gap: 1em;

    &:nth-child(even) &--screens {
      grid-area: 1 / 2 / 1 / 3;
    }

    &:nth-child(even) &--info {
      justify-self: start;
    }

    &--info {
      align-self: center;
      grid-template-columns: auto 1fr;
      align-items: center;
      max-width: 32em; // 512px
      justify-self: end;
      .info--title {
        grid-column: 1 / -1;
        &::before {
          bottom: -6px;
        }
      }

      .github {
        grid-area: 4 / 2 / 4 / 3;
        margin-left: 2em;
        justify-self: start;
      }

      .btn {
        grid-column: 1 / 2;
        justify-self: start;
      }

      .keys {
        margin-bottom: 2em;
      }
    }
  }
}

@include tabletLg {
  .portfolio--content__card {
    grid-gap: 0em;
    gap: 0em;
  }
}

// Screen Animation KeyFrames

@-webkit-keyframes scrollPage {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 19em));
    transform: translateY(calc(-100% + 19em));
  }
}
@keyframes scrollPage {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 19em));
    transform: translateY(calc(-100% + 19em));
  }
}

@-webkit-keyframes scrollPageKeep {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 10em));
    transform: translateY(calc(-100% + 10em));
  }
}
@keyframes scrollPageKeep {
  from {
    -webkit-transform: translateY(0px);
    transform: translateY(0px);
  }
  to {
    -webkit-transform: translateY(calc(-100% + 10em));
    transform: translateY(calc(-100% + 10em));
  }
}

