.skills {
  @include bgIMG($clr-dark);
  padding: 12em 1em;
}

.skills--tabs {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  justify-items: center;
  align-items: end;
  grid-gap: 1.2em;
  gap: 1.2em;
  margin-top: 3.5em;

  & > :last-child {
    grid-column: 3/ 4;
  }
  & > :nth-child(9) {
    grid-column: 2/ 3;
  }

  & > :nth-child(8) {
    .skills--tabs__tab--img {
      width: 2.1em;
    }
  }

  &__tab {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;

    .icon {
      font-size: 2.5rem;
      pointer-events: none;
    }

    &--img {
      height: auto;
      width: 2.85em;
    }

    &--text {
      font-size: var(--fs-small);
      text-transform: uppercase;
    }
  }
}

@include mediaTablet {
  .skills {
    padding: 16em 1em;
  }

  .skills--content {
    display: flex;
    flex-direction: column;
    align-items: center;

    .title {
      text-align: center;
      .bottom::before {
        left: 35%;
      }
    }
  }

  .skills--tabs {
    grid-template-columns: repeat(5, 1fr);
    row-gap: 3.5em;
    grid-row-gap: 3.5em;
    & > :last-child {
      grid-column: auto;
    }
    & > :nth-child(9) {
      grid-column: auto;
    }

    & > :nth-child(8) {
      .skills--tabs__tab--img {
        width: 3em;
      }
    }

    &__tab {
      .icon {
        font-size: 3rem;
      }

      &--img {
        width: 4em;
      }

      &--text {
        font-size: var(--fs-small);
      }
    }
  }
}

sources.scss\\3-layout\\_footer.scss:
.footer {
  @include bgIMG($clr-dark, 0.97);

  &--content {
    display: grid;
    justify-items: center;
    align-items: center;
    grid-gap: 1.5em;
    gap: 1.5em;
    padding: 2em 0;
  }

  &--logo {
    img {
      width: 50px;
    }
  }

  .btn {
    color: var(--clr-primary);
    &:hover,
    &:focus {
      color: var(--clr-white);
    }
  }

  &--icons .icon {
    margin: 0 1em;
  }

  &--copyright {
    text-align: center;
    width: 100%;
    background-color: var(--clr-dark-light);
    padding: 0.5em 0;
  }
}

.footer--nav {
  display: flex;
  justify-content: space-around;
  width: 100%;

  .nav--links {
    position: relative;
    margin-right: 0;
    font-weight: var(--fw-semi);
    border-bottom: 2px solid transparent;
    cursor: pointer;
    @include cubicBezier;

    &:hover,
    &:focus {
      border-bottom-color: var(--clr-primary);
    }

    &:last-child {
      margin-right: 0;
    }
  }
}

@include mediaTablet {
  .footer {
    &--content {
      grid-template-columns: 1fr auto 1fr;
      grid-auto-rows: auto;
    }

    .btn {
      grid-area: 2 / 2 / 3 / 3;
    }

    &--icons {
      grid-area: 1 / 1/ 2 /2;
      display: flex;
      flex-wrap: wrap;
      justify-content: space-around;
    }
  }

  .footer--nav {
    display: flex;
    flex-wrap: wrap;
  }
}
